settings.colorFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'colorpin') {
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120 * 100;
	    return tinycolor.mix('blue', 'red', percent).toHexString();
	}
	return 'blue';
}

settings.markerImageFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'thermometer') {
	var res = {
	    url: images[0],
	    size: 40
	}
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120;
	    var index = Math.min(3, Math.floor(4 * percent));
	    res.url = images[index];
	}
	return res;
}

