const $injector = widgetContext.$scope.$injector;
const currentUser = widgetContext.currentUser;
const entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
const attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
const customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
const utils = $injector.get(widgetContext.servicesMap.get('utils'));

let userGroupsIds, userGroups;
let hasPermissionToEdit = false;

entityGroupService.getEntityGroupIdsForEntityId('USER', currentUser.userId).subscribe(groupsIds => {
    userGroupsIds = groupsIds;

    const idsArray = [];

    userGroupsIds.forEach(groupId => {
        idsArray.push(groupId.id);
    });

    entityGroupService.getEntityGroupsByIds(idsArray).subscribe(groups => {
        userGroups = groups;

        hasPermissionToEdit = userGroups.some(group => group.name === 'Tenant Administrators' || group.name === 'Customer Administrators');

        openEditEntityDialog();
    });
});

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditDeviceDialogController).subscribe();
}


