    function setEntityAttributes(attributes) {
        for (let i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function processData() {
        initImagesView();
        if (typeof vm.attributes.meterPhoto !== 'undefined') {
            try {
                vm.imagesData = JSON.parse(vm.attributes.meterPhoto);
            } catch (e) {
                vm.imagesData.imagesList.push({url: vm.attributes.meterPhoto});
                vm.imagesData.mainImageIndex = 0;
            }
        }
    }

    function initImagesView() {
        vm.multiplePhotosView = true;
        vm.isEditMode = vm.isEditMode || false;
        vm.imageCarousel = {
            index: 0
        }
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.loading = true;

        vm.editEntityFormGroup.markAsPristine();
        saveAttributes(entityId).subscribe(()=> {
            vm.loading = false;
            vm.dialogRef.close(null);
        });
    };

    vm.trackByIndex = function(index) {
        return index;
    }

    vm.switchLayout = function (multiple) {
        vm.multiplePhotosView = multiple;
        if (!multiple) {
            vm.imageCarousel.index = 0;
        }
    };

    vm.addNewImages = function() {
        vm.editEntityFormGroup.patchValue({meterPhoto: null});
        vm.isEditMode = true;
    };

    vm.applyChanges = function() {
        let newImage = {
            url: vm.editEntityFormGroup.get('meterPhoto').value
        };
        vm.imagesData.imagesList.push(newImage);
        vm.imageCarousel.index = vm.imagesData.imagesList.length - 1;
        vm.isEditMode = false;
        vm.editEntityFormGroup.markAsDirty();
    };

    vm.discardChanges = function() {
        vm.isEditMode = false;
    };

    vm.deleteImage = function (index) {
        if (index > -1) {
            vm.imagesData.imagesList.splice(index, 1);
            if (vm.imagesData.imagesList.length) {
                vm.imagesData.mainImageIndex = 0;
            } else {
                vm.imagesData.mainImageIndex = -1;
            }
            vm.editEntityFormGroup.markAsDirty();
        }
    };

    vm.selectAsMain = function (index) {
        vm.imagesData.mainImageIndex = index;
        vm.editEntityFormGroup.markAsDirty();
    }

    vm.deselectAsMain = function () {
        vm.imagesData.mainImageIndex = 0;
        vm.editEntityFormGroup.markAsDirty();
    }

    vm.onImagesCarouselIndexChanged = function() {

    }

