    // };

    function saveAttributes(entityId) {
        let imagesDataToSave;
        if (vm.imagesData.imagesList.length) {
            imagesDataToSave = JSON.stringify(vm.imagesData);
        } else {
            imagesDataToSave = null;
        }

        const attributesArray =[{key: 'meterPhoto', value: imagesDataToSave}];

        if (vm.attributes.meterPhoto !== imagesDataToSave) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }

    vm.getCustomTranslation = function(translationValue, defaultValue) {
        let translation;
        if (translationValue) {
            translation = utils.customTranslation(translationValue, translationValue);
            if (translation.includes("{i18n")) {
                translation = defaultValue;
            }
        } else {
            translation = defaultValue;
        }
        return translation;
    };
